package Patterns;
import java.util.Scanner;
public class Practice{

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s=new Scanner(System.in);
		int n=s.nextInt();
		int row=2*n-1;
		int col=2*n-1;
		int i=0,j=0;
		while(i<row) {
			while(j<col) {
				if(i==0||i==2*n-2) {
					System.out.print(n);
				}
				
				
			}
		}
		}
}
